from src import app


class EmailHtml:

    @staticmethod
    def email_activation(code):
        html = f"""
        <div>
          <img src='data:image/png;base64,' width='150' alt="">
          <h2>Email exposure</h2>
          <p>
            a user on our site was created by your e-mail, if it was you then click on
              <a href='{app.config['FRONT_LINK']}/email/?code={code}' style='color: #ba6f0b'>
                confirm mail
            </a>
          </p>
          <p>
            or if it was not you and you want to refute the registration of this user, then click on
              <a href='' style='color: #ba6f0b'>
                refute the registration on my e-mail
              </a>
          </p>
        </div>
        """
        return html

    @staticmethod
    def password_restore(code):
        html = f"""
            <div>
              <img src='data:image/png;base64,' width='150' alt="">
              <h2>Password recovery</h2>
              <p>
                to recover your profile password, click on
                  <a href='{app.config['FRONT_LINK']}/guest/?restore_password_security_code={code}' style='color: #ba6f0b'>
                    restore password
                  </a>
              </p>
            </div>
            """
        return html
